import React, { useEffect, useReducer, useState } from "react";
import { ApiApi, Job } from "../../../util/gen/api/dist";
// import { Editor, EditorState } from "draft-js";
import { convertToRaw, EditorState, ContentState } from "draft-js";
import { Editor, RawDraftContentState } from "react-draft-wysiwyg";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";

import "draft-js/dist/Draft.css";
import "./OutReachEditor.css";
import JobCell from "../../common/JobCell/JobCell";
import { RouteComponentProps, useParams } from "react-router";
import reducer, { OutReachEditorReducerState } from "./OutReachEditorReducer";
import AllJobsPage from "../Jobs/allJobs";
import getEditorContentAsString from "../../../util/HelperFunctions/GetTextFromEditor";

type OutReachEditorProps = {
  setQueryInfo?: Function;
  apiService?: ApiApi;
};

interface IUserPublicProfileRouteParams {
  id: string;
}

const OutReachEditorPage: React.FC<OutReachEditorProps> = (props) => {
  const { apiService } = props;
  const [job, setJob] = useState<Job>();
  const id = parseInt(useParams<IUserPublicProfileRouteParams>().id);

  const [state, dispatch] = useReducer(reducer, {
    job: job,
    editorState: EditorState.createWithContent(
      ContentState.createFromText(job?.template ?? "job null")
    ),
    isDirty: false,
  } as OutReachEditorReducerState);

  useEffect(() => {
    apiService?.apiJobsRetrieve(id).then((value) => {
      setJob(value.data);
      var initialContentState = ContentState.createFromText(
        value.data.template ?? ""
      );
      var initialEditorState = EditorState.createWithContent(
        initialContentState
      );
      dispatch({ type: "EDITOR_LOADED_200", payload: initialEditorState });
    });
  }, []);

  useEffect(() => {
    if (
      state.isDirty === true &&
      state.editorState?.getCurrentContent() != undefined
    ) {
      const value = getEditorContentAsString(state.editorState);
      if (job?.template !== value) {
        apiService
          ?.apiJobsUpdate(id, {
            ...job,
            template: value,
          } as Job)
          .then((value) => {
            debugger;
            setJob(value.data);
            dispatch({ type: "EDITOR_POSTED" });
          });
      } else {
        dispatch({ type: "EDITOR_POSTED" });
      }
    }
  }, [state.isDirty]);

  const saveClickHandler = () => {
    if (state.editorState != undefined) {
      const value = getEditorContentAsString(state.editorState);
      save(value);
    }
  };
  const save = (value: string) => {
    apiService
      ?.apiJobsUpdate(id, {
        ...job,
        template: value,
      } as Job)
      .then((value) => {
        debugger;
        setJob(value.data);
        dispatch({ type: "EDITOR_POSTED" });
      });
  };

  const onEditorStateChange = (editorState: EditorState) => {
    dispatch({ type: "EDITOR_EDITED_200", payload: editorState });
  };

  const init = ContentState.createFromText("plain text");

  return (
    <div>
      <div>
        <h2>{"Draft for " + job?.name}</h2>
        <button onClick={saveClickHandler} disabled={!state.isDirty}>
          Save Template
        </button>
      </div>
      <Editor
        initialContentState={convertToRaw(init)}
        editorState={state.editorState}
        onEditorStateChange={onEditorStateChange}
        spellCheck={true}
        wrapperClassName="demo-wrapper"
        editorClassName="demo-editor"
      />
    </div>
  );
};

export default OutReachEditorPage;
