import React, { useEffect, useState } from "react";
import { ApiApi, Job } from "../../../util/gen/api/dist";
// import { Editor, EditorState } from "draft-js";
import { EditorState } from "draft-js";
import { Editor } from "react-draft-wysiwyg";
import "react-draft-wysiwyg/dist/react-draft-wysiwyg.css";

import "draft-js/dist/Draft.css";
import "./OutReachEditor.css";
import JobCell from "../../common/JobCell/JobCell";
import { RouteComponentProps, useParams } from "react-router";

type OutReachEditorProps = {
  setQueryInfo?: Function;
  apiService?: ApiApi;
};

interface IUserPublicProfileRouteParams {
  id: string;
}

const OutReachEditorPage: React.FC<OutReachEditorProps> = (props) => {
  const { apiService } = props;
  const [editorState, setEditorState] = React.useState(() =>
    EditorState.createEmpty()
  );
  const [job, setJob] = useState<Job>();
  const onchange = (editorState: EditorState) => {
    setEditorState(editorState);
  };

  const id = useParams<IUserPublicProfileRouteParams>().id;
  debugger;
  useEffect(() => {
    apiService?.apiJobsRetrieve(parseInt(id)).then((value) => {
      debugger;
      setJob(value.data);
    });
  }, []);

  return (
    <div>
      <div>
        <h2>{"Draft for" + job?.name}</h2>
        <button>Save Template</button>
      </div>
      <Editor
        editorState={editorState}
        onEditorStateChange={onchange}
        spellCheck={true}
        wrapperClassName="demo-wrapper"
        editorClassName="demo-editor"
      />
    </div>
  );
};

export default OutReachEditorPage;
