import React from "react";
import { propTypes } from "react-bootstrap/esm/Image";
import { EditorState, RawDraftContentState } from "draft-js";
import { Client, Job } from "../../../util/gen/api/dist";

export {};

export type OutReachEditorReducerState = {
  job: Job;
  editorState?: EditorState;
  isDirty: boolean;
  editorRawState?: RawDraftContentState;
  isModalOpen: boolean;
  isDraft?: boolean;
};

export type OutReachEditorActions =
  | {
      type: "EDITOR_EDITED_200";
      payload: EditorState;
    }
  | {
      type: "EDITOR_ON_CHANGE";
      payload: RawDraftContentState;
    }
  | {
      type: "EDITOR_POSTED";
    }
  | {
      type: "OPEN_MODAL";
    }
  | {
      type: "CLOSE_MODAL";
      payload: EditorState;
    }
  | {
      type: "EDITOR_LOADED_200";
      payload: EditorState;
    };

export default function reducer(
  state: OutReachEditorReducerState,
  action: OutReachEditorActions
): OutReachEditorReducerState {
  debugger;
  switch (action.type) {
    case "EDITOR_EDITED_200":
      return { ...state, editorState: action.payload, isDirty: true };
    case "EDITOR_LOADED_200":
      return {
        ...state,
        isDirty: false,
        editorState: action.payload,
      };
    case "EDITOR_ON_CHANGE":
      return {
        ...state,
        editorRawState: action.payload,
        isDirty: true,
      };
    case "EDITOR_POSTED":
      return {
        ...state,
        isDirty: false,
      };

    case "OPEN_MODAL":
      return {
        ...state,
        isModalOpen: true,
      };

    case "CLOSE_MODAL":
      return {
        ...state,
        editorState: action.payload,
        isModalOpen: false,
      };
    default:
      return state;
  }
}
